
public class newcheckanother {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int x = 2 , y =1 ,z=-1 , w = 0 ;
	boolean p;
	p= w++==1 || --y==0;
	System.out.println(p);
	System.out.println(x);
	System.out.println(y);
	System.out.println(z);
	System.out.println(w);
	p = (x>2.0) && z++>=0; 
	System.out.println(p);
	System.out.println(x);
	System.out.println(y);
	System.out.println(z);
	System.out.println(w);
	p=y-->1&& (x)>7;
	System.out.println(p);
	System.out.println(x);
	System.out.println(y);
	System.out.println(z);
	System.out.println(w);
	p= y-->0 || (x==3) ;
	System.out.println(p);
	System.out.println(x);
	System.out.println(y);
	System.out.println(z);
	System.out.println(w);
		
	}

}
